//Back-end complete function template for C++

class Solution{   
public:
    //Function to find the only odd occurring element in an array.
    int getOddOccurrence(int arr[], int n) {
        int res = 0;
        //iterating over all elements of the array.
        for (int i = 0; i < n; i++) 
            //using XOR operation to find the odd occurring element.
            res = res ^ arr[i];
        //returning the odd occurring element.
        return res;
    }
};

java.full_func:
class Solution{
    // Method to find the element with odd occurrence in given array
    int getOddOccurrence(int[] arr, int n) {
        int res = 0; // Initialize result as 0

        // Performing XOR operation on each element in the array
        // XOR operation returns the number with odd occurrence
        for (int i = 0; i < n; i++) 
            res = res ^ arr[i];

        // Returning the result
        return res;
    }
}

