// Declaration of Solution class
class Solution {
  public:
    // Function to find the remainder when N is divided by 7
    int findRemainder(string N) {

        // Creating an array to store the remainder obtained
        // when 10 power i is divided by 7.
        int a[1000];

        // Initializing the first element of array with 1
        a[0]=1;

        // Loop to calculate the remainder for each power of 10
        for(int i=1 ; i<1000 ; i++)
            a[i]=(a[i-1]*10)%7;

        // Initializing the result with 0
        int res = 0;

        // Loop to calculate the remainder for each digit in N
        for(int i=N.length()-1 ; i>=0 ; i--)
            res = (res + (N[i]-'0') * a[N.length()-1-i]) % 7;

        // Returning the final remainder
        return res;
    }
};

