class Solution {
    static int findRemainder(String N) {
        
        // Creating an array to store the remainder obtained
        // when 10 power i is divided by 7.
        int[] a = new int[1000];
        
        a[0]=1;
        for(int i=1 ; i<1000 ; i++)
            a[i]=(a[i-1]*10)%7;
            
        int res = 0;
        for(int i=N.length()-1 ; i>=0 ; i--)
            res = (res + (N.charAt(i)-'0') * a[N.length()-1-i]) % 7;
            
        return res;
    }
};

