//User function Template for javascript
/**
 * @param {number} N
 * @returns {number}
*/

class Solution
{
    //Function to find remainder
    findRemainder(N)
    {
        // Creating an array to store the remainder obtained
        // when 10 power i is divided by 7.
        let a = new Array(1000).fill(0); 

        a[0]=1;

        for (let i=1; i<1000; i++) {
            a[i]=(a[i-1]*10)%7;
        }

        let res = 0;
        for (let i=N.length-1; i>=0; i--) {
            res = (res + (parseInt(N[i])-0) * a[N.length-1-i]) % 7;
        }

        return res;
    }
}
