class Solution{
	public:
	bool check(long long int a,long long int b)
	{
		long long int c = 0;

		// calculate the sum of digit 
		while(a != 0)
		{
				c = c+ a%10;
				a = a/10;
		}
		// if yes 
		if(c == b)return 1;
		//if no 
		return 0;
	}


	long long int Root(long long int n)
	{
		bool found=0;
		long long int  mx = 1e18;


		for(long long int i = 0;i <= 90;i++)
		{

				long long int s = i*i+4*n;
				long long int sq = sqrt(s);


				if(sq*sq == s && check((sq-i)/2,i))
				{
					found=1;
					mx = min(mx,(sq-i)/2);

				}
		}


		if(found)
		return mx;
		else
		return -1;
	}
};
