//Back-end complete function Template for C

void swap(int *xp, int *yp) 
{ 
    int temp = *xp; 
    *xp = *yp; 
    *yp = temp; 
} 

void transpose(int N, int matrix[][N])
{ 
    for (int i = 0; i < N; i++) 
        for (int j = i+1; j < N; j++) 
            swap(&matrix[i][j], &matrix[j][i]); 
}
