class Solution {
  public:
    vector<int> findXandY(int A , int B , int N) {
        
        int x=-1,y=-1;    // Initializing x and y as -1.
        
        // Traversing from 0 to n to check if a solution exists.
        for (int i = 0; i * A <= N; i++)
        {
            if ((N - (i * A))>=0 && (N - (i * A)) % B == 0)
            {
                x = i;                     // values of x and y are updated
                y = (N - (i * A)) / B;     // when we find a solution.
                break;
            }
        }
        
        vector<int> arr(2);
        arr[0] = x;
        arr[1] = y;
        
        return arr;
    }
};

