/**
 * @param {number} A
 * @param {number} B
 * @param {number} N
 * @returns {number[]}
*/

class Solution {
    //Function to find X and Y
    findXandY(A, B, N)
    {
        let x = -1; //Initializing x as -1.
        let y = -1; //Initializing y as -1.

        // Traversing from 0 to n to check if a solution exists.
        for (let i = 0; i <= Math.floor(N / A); i++) {
            if ((N - (i * A)) >= 0 && (N - (i * A)) % B === 0) {
                x = i; // values of x and y are updated
                y = (N - (i * A)) / B; // when we find a solution.
                break;
            }
        }

        let arr = [x, y];

        return arr;
    }
}

class Solution
{
    public List<int> findXandY(int A, int B, int N)
    {
        int x = -1;
        int y = -1;

        for (int i = 0; i * A <= N; i++)
        {
            if ((N - (i * A)) >= 0 && (N - (i * A)) % B == 0)
            {
                x = i;
                y = (N - (i * A)) / B;
                break;
            }
        }

        List<int> arr = new List<int>();
        arr.Add(x);
        arr.Add(y);

        return arr;
    }
}
