class Solution
{
public:
    //Function to count the number of fingers raised based on the input.
    int fingerCount(int N)
    {
        //Finding the remainder of N when divided by 8.
        int r = N % 8;

        //Checking various cases and returning the corresponding finger count.
        if (r == 1)
            return r;
        if (r == 5)
            return r;
        if (r == 0 || r == 2)
            return 2;
        if (r == 3 || r == 7)
            return 3;
        if (r == 4 || r == 6)
            return 4;
    }
};
