//Back-end complete function template for C++

class Solution{
public:
    //Function to check if a number is a power of two.
    bool powerOfTwo(long long int n) { 
        return (!(n & n-1)); 
    } 

    //Function to check if only the first and last bits of a number are set.
    int onlyFirstAndLastAreSet(long long int n) { 
        if (n == 1) 
            return true; 
        if (n == 2) 
            return false;
        if(powerOfTwo(n - 1)) return 1;
        return 0;  


    } 

};

//Back-end complete function template for C#

class Solution{
    public bool powerOfTwo(long n) { 
        return (n != 0) && ((n & (n - 1)) == 0); 
    } 

    public int onlyFirstAndLastAreSet(long n) { 
        if (n == 1) 
            return 1; 
        if (n == 2) 
            return 0;
        if(powerOfTwo(n - 1)) return 1;
        return 0;  
    } 
}
