// Backend complete function template for C++

class Solution
{
  public:
    // Function to get the first digit of the product of all elements in the array
    int firstDigit (int arr[], int n)
    {
        long double S = 0;

        // Calculating the sum of logarithms of all elements in the array
        for (int i=0; i<n; i++)
            S = S + log10(arr[i]*1.0);       
        S += 0.000001;

        // Getting the fractional part of the sum
        long double fract_S = S - floor(S);

        // Getting the first digit by raising 10 to the power of the fractional part
        int ans = pow(10, fract_S);
        return ans;
    }
};

