//User function Template for javascript
/**
 * @param {number[]} arr
 * @param {number} n
 * @returns {number}
*/

class Solution {
    //Function to return the first digit of the array.
    firstDigit(arr, n)
    {
        let S = 0;

        // Calculating the sum of logarithms of array elements
        for (let i = 0; i < n; i++) {
            S += Math.log10(arr[i]);
        }

        S += 0.000001;

        // Subtracting the floor value of the sum
        S -= Math.floor(S);

        // Calculating the first digit of the product
        let ans = Math.pow(10, S);
        return parseInt(ans);
    }
}
