// Function to return the position of the first repeating element.
int firstRepeated(int arr[], int n) {
    // Assuming a large enough range of integers; you can extend it further
    int min_val = INT_MAX, max_val = INT_MIN;

    for (int i = 0; i < n; i++) {
        if (arr[i] < min_val)
            min_val = arr[i];
        if (arr[i] > max_val)
            max_val = arr[i];
    }

    int range = max_val - min_val + 1;
    int *freq = (int *)calloc(range, sizeof(int));
    if (!freq) {
        fprintf(stderr, "Memory allocation failed for frequency array\n");
        return -1;
    }

    // Storing the frequency of each element in the frequency array.
    for (int i = 0; i < n; i++) {
        freq[arr[i] - min_val]++;
    }

    // Iterating over the array elements.
    int ans = -1;
    for (int i = 0; i < n; i++) {
        // If frequency of current element is greater than 1,
        // then store the 1-based index and break the loop.
        if (freq[arr[i] - min_val] > 1) {
            ans = i + 1; // 1-based index
            break;
        }
    }

    free(freq); // Free the frequency array
    return ans;
}

