class Solution {
    // Function to return the position of the first repeating element
    public int FirstRepeated(int[] arr, int n) {
        // Dictionary to store the frequency of each element
        Dictionary<int, int> freq = new Dictionary<int, int>();

        // Storing the frequency of each element in the dictionary
        for (int i = 0; i < n; i++) {
            if (freq.ContainsKey(arr[i])) {
                freq[arr[i]]++;
            } else {
                freq[arr[i]] = 1;
            }
        }

        // Iterating over the array elements to find the first repeated element
        for (int i = 0; i < n; i++) {
            if (freq[arr[i]] > 1) {
                return i + 1; // 1-based index
            }
        }

        return -1; // No repeating element found
    }
}

