//Back-end complete function Template for C++
class Solution{
public:
    //Function to check if arr[] can be fit in brr[] based on sorting.
    bool isFit(int arr[], int brr[], int n){
        //sorting the arrays arr[] and brr[].
        sort(arr,arr+n);
        sort(brr,brr+n);
        int flag = 0;

        //iterating over the arrays to check if each element in arr[] is less than or equal to the corresponding element in brr[].
        for(int i=0;i<n;i++)
        {
            //if any element in arr[] is greater than the corresponding element in brr[], set flag=1 and break the loop.
            if(!(arr[i]<=brr[i]))
            {
                flag=1;
                break;
            }
        }

        //if flag=1, return false, else return true.
        if(flag==1)
            return false;
        return true;
    }
};

