class Solution{

    // Function for finding maximum and value pair
    public static boolean isFit (int arr[], int brr[], int n) {
        //Sorting the arrays in ascending order
        Arrays.sort(arr);
        Arrays.sort(brr);

        //Initializing a flag variable to keep track of any mismatches
        int flag = 0;

        //Comparing the elements of the arrays one by one
        for(int i=0;i<n;i++)
        {
            //If any element in the first array is greater than the corresponding element in the second array,
            //set the flag to 1 and break the loop
            if(!(arr[i]<=brr[i]))
            {
                flag=1;
                break;
            }
        }

        //If the flag is 1, it means there was a mismatch and the arrays are not fit for each other
        //Return false. Otherwise, return true.
        if(flag==1)
            return false;
        return true;
    }

}

