javascript.full_func:
class Solution {
    isFit(arr, brr, n) {
        const sortedArr = [...arr].sort((a, b) => a - b);
        const sortedBrr = [...brr].sort((a, b) => a - b);

        for(let i = 0; i < n; i++) {
            if(sortedArr[i] > sortedBrr[i]) {
                return false;
            }
        }

        return true;
    }
}
