// Calculate the list of flipped bits for a given number N
class Solution{
    static List<String> flipBits(long N){
        List<String> sol = new ArrayList<>();
        long c = (long)Math.pow(2, 32); // Calculate the maximum value of N
        N = c - N - 1; // Calculate the flipped version of N
        String s = Long.toString(N); // Convert N to a string
        sol.add(s); // Add the string representation of N to the solution list
        String st = ""; // Initialize an empty string to store the binary representation of N
        // Calculate the binary representation of N
        while(N > 0){
            st += (char)(N%2 + '0'); // Append the least significant bit of N to st
            N /= 2; // Divide N by 2 to get the next bit
        }
        sol.add(st); // Add the binary representation of N to the solution list
        return sol; // Return the solution list
    }
}

