// Function to print the Floyd Triangle
void printFloydTriangle(int N) {
    int i, j, val = 1;

    // Iterating through each row of the triangle
    for (i = 1; i <= N; i++) {
        // Printing the values in each row
        for (j = 1; j <= i; j++)
            printf("%d ", val++);

        // Printing a new line after each row
        printf("\n");
    }
}

class Solution {
  public:
    void printFloydTriangle(int N) {
        int ans = 1;
        for (int i = 1; i <= N; i++) {
            for (int j = 1; j <= i; j++) {
                cout << ans++ << " ";
            }
            cout << endl;
        }
    }
};
