class Solution
{
    //Complete this function
    public long Nth_mileSpeed(int n)
    {
        long a = 1;
        long b = 7;
        long c = 3;
        long ans_odd = 0;
        long ans_even = 0;

        //looping from 1 to n to calculate the nth speed
        for(int i = 1; i <= n; i++){
            //if i is odd, calculate the ith odd speed
            if(i % 2 != 0){
                ans_odd = a * a;
                a += c;
                c += 2;
            }
            //if i is even, calculate the ith even speed
            else{
                ans_even = b;
                b *= 3;
            }
        }

        //returning the appropriate speed based on if n is even or odd
        return (n % 2 != 0) ? ans_odd : ans_even;
    }
}
