//Back-end complete function Template for C++

forward_list<int> eraseMe(forward_list<int> fwdlist) {

    auto it = fwdlist.begin();    // 10 20 30 40
                                  //   ^
                                  //Position of "it"
    it = fwdlist.erase_after(it); // 10 30 40
                                  //      ^
                                  // Updated position of "it
    return fwdlist;
}
