// Function to iterate over elements of a vector and print them
void iter(vector<int>::iterator it1, vector<int>::iterator it2)
{
    // Looping through the vector using an iterator
    for (auto it = it1; it != it2; it++)
    {
        // Printing the element pointed by the iterator
        cout << *it << " ";
    }
    // Printing a new line after printing all elements
    cout << endl;
}
