// Back-end complete function Template for C++

class Solution{
public:
    // Function to check if a number is prime adjacent or not.
    int primeAdjacent(int N){
        if(N%2 == 1 || N == 2)
            return 0;
        else{
            // checking if N-1 is divisible by any number between 2 and sqrt(N-1).
            for(int i = 2;i <= sqrt(N-1);i++)
                if((N-1)%i == 0)
                    return 0;

            // checking if N+1 is divisible by any number between 2 and sqrt(N+1).
            for(int i = 2;i <= sqrt(N+1);i++)
                if((N+1)%i == 0)
                    return 0;
        }
        // return 1 if the number is prime adjacent, else return 0.
        return 1;
    }
};

//User function Template for C#

class Solution
{
    // Function to check if a number is prime adjacent or not.
    public bool primeAdjacent(int N)
    {
        if(N%2 == 1 || N == 2)
            return false;
        else{
            // checking if N-1 is divisible by any number between 2 and sqrt(N-1).
            for(int i = 2;i <= Math.Sqrt(N-1);i++)
                if((N-1)%i == 0)
                    return false;

            // checking if N+1 is divisible by any number between 2 and sqrt(N+1).
            for(int i = 2;i <= Math.Sqrt(N+1);i++)
                if((N+1)%i == 0)
                    return false;
        }
        // return true if the number is prime adjacent, else return false.
        return true;
    }
}
