class Complete{
    // XOR operation is performed on adjacent elements in the array
    // to find the maximum value in each pair
    public static int[] game_with_number (int arr[], int n) {
        // Traverse through the array until second to last element
        for(int d=0;d < n-1;d++)
        {
            // XOR the current element with the next element
            arr[d] = arr[d]^arr[d+1];
        }
        // Return the modified array
        return arr;
    }
}

