#include <stdio.h>

int main() {
    int a, b;
    scanf("%d %d", &a, &b);
    // code here
    int small = (a < b) ? a : b; // Using ternary operator to find minimum
    int ans = 1;

    for (int i = 1; i <= small; i++) {
        if (a % i == 0 && b % i == 0)
            ans = i;
    }

    printf("%d\n", ans);

    return 0;
}

