//Back-end complete function Template for C++

class Solution{   
public:
    //Function to find gcd of two numbers and calculate fibonacci modulo 100.
    int fibGcd(int M, int N){
        //calculating gcd of M and N.
        int x = __gcd(N, M);
        int a = 0, b = 1;

        //iterating x number of times.
        while(x--){
            //calculating fibonacci number modulo 100.
            int c = a + b;
            c % 100;
            a = b;
            b = c;
            a %= 100, b %= 100;
        }
        //returning fibonacci modulo 100.
        return a;
    }
};
