//Back-end complete function Template for C

int min(int num1, int num2) 
{
    return (num1 > num2 ) ? num2 : num1;
}

int gcd(int a, int b){
    int small = min(a, b);
    int ans = 1;
    for(int i = 1; i <= small; i++) {
        if (a % i == 0 && b % i == 0)
            ans = i;
    }
    return ans;
}

