// Back-end complete function Template for C++

class Solution{
public:
    // Function to calculate the Mth half of N
    int mthHalf(int N, int M){
        // Calculating the highest power of 2 less than N
        int x = log(N)/log(2);

        // If M is greater than the highest power of 2 plus one, return 0
        if(x + 1 < M)
            return 0;

        // Calculating the Mth half of N
        int y = N / pow(2, M-1);

        // Returning the Mth half
        return y;
    }
};

javascript.full_func:
//User function Template for javascript

class Solution{
    mthHalf(n, m){
        let x = Math.log(n) / Math.log(2);
        if (x + 1 < m) {
            return 0;
        }
        let y = Math.floor(n / Math.pow(2, m-1));
        return y;
    }
}
