// Back-end complete function Template for C++

class Solution{
public:
    // Function to find the nth term in the sequence
    int nthTerm(int n){

        int mod = 1000000007; // modular value for taking modulo
        int x = 7; // starting term of the sequence

        // loop to iterate and calculate the nth term
        for(int i = 1; i < n; i++){
            x = ((x*2)%mod + i)%mod; // formula to calculate the next term
        }

        return x; // returning the nth term
    }
};

