class Solution {
  public:
    int minCost(vector<int>& height) {
        // Code here
        int n = height.size();
        if (n == 1)
            return 0;
        if (n == 2)
            return abs(height[0] - height[1]);
        int prev2 = 0, prev = abs(height[0] - height[1]), curr = 0;
        for (int i = 2; i < n; i++) {
            int jump1 = prev + abs(height[i - 1] - height[i]);
            int jump2 = prev2 + abs(height[i - 2] - height[i]);
            curr = min(jump1, jump2);
            prev2 = prev;
            prev = curr;
        }
        return curr;
    }
};

