class Solution {
    minCost(height) {
        const n = height.length;

        // Base cases
        if (n === 1) return 0;
        if (n === 2) return Math.abs(height[0] - height[1]);

        // Initialize variables to store the minimum energy for the two previous stairs
        let prev2 = 0;
        let prev = Math.abs(height[0] - height[1]);
        let curr = 0;

        // Iterate through the stairs starting from the third stair
        for (let i = 2; i < n; i++) {
            // Calculate energy if jumping from the (i-1)th stair
            const jump1 = prev + Math.abs(height[i - 1] - height[i]);

            // Calculate energy if jumping from the (i-2)th stair
            const jump2 = prev2 + Math.abs(height[i - 2] - height[i]);

            // Choose the minimum of the two possible jumps
            curr = Math.min(jump1, jump2);

            // Update the previous two stairs for the next iteration
            prev2 = prev;
            prev = curr;
        }

        return curr;
    }
}
