// Back-end complete function template for C++

class Solution {
  public:
    int isGoodorBad(string S) {
        int vow = 0, con = 0, flag = 0;
        for (int i = 0; i < S.length(); i++) {
            if (S[i] == 'a' || S[i] == 'e' || S[i] == 'i' || S[i] == 'o' ||
                S[i] == 'u') {
                vow++; // Incrementing count of vowels if it's a Vowel
                con = 0; // Setting count of Consonants to 0
            } else if (S[i] != 'a' && S[i] != 'e' && S[i] != 'i' &&
                       S[i] != 'o' && S[i] != 'u' && S[i] != '?') {
                vow = 0; // Setting count of Vowels to 0
                con++; // Incrementing count of consonants
            }
            if (S[i] == '?') {
                con++; // Incrementing count of both as '?' can be both
                vow++;
            }
            // If at any Point the Count of Vowels is more than 5 or that of
            // Consonants is more than 3, then we know it's a BAD String,
            // else it's GOOD.
            if (vow > 5 || con > 3) {
                flag = 1;
                break;
            }
        }
        if (flag == 1)
            return 0;
        else
            return 1;
    }
};

