 * @returns {number}
*/

class Solution {
    //Function to check whether the string is good or bad.
    isGoodorBad(S){
        let vow=0;
        let con=0;
        let flag=0;
        for (let i=0; i<S.length; i++){
            if(S[i]=='a' || S[i]=='e' || S[i]=='i' || S[i]=='o' || S[i]=='u'){
                vow+=1;       //Incrementing count of vowels if it's a Vowel
                con=0;
            }
            else if(S[i]!='a' && S[i]!='e' && S[i]!='i' && S[i]!='o' && S[i]!='u' && S[i]!='?'){
                vow=0;        //Setting count of Vowels to 0
                con+=1;       //Incrementing count of consonants
            }
            if(S[i]=='?'){
                con+=1;       //Incrementing count of both as '?' can be both
                vow+=1;
            }
            //If at any Point the Count of Vowels is more than 5 or that of
            //Consonants is more than 3, then we know it's a BAD String,
            //else it's GOOD.
            if(vow>5 || con>3){
                flag=1;
                break;
            }
        }
        if(flag==1){
            return 0;
        }
        else{
            return 1;
        }
    }
}
