// Back-end complete function Template for C++

class Solution{
    public:
    //Function to count the number of set bits in a number and multiply it with the number.
    int bitMultiply(int N){
        int count = 0, n = N;

        //Loop to count the number of set bits in the number N.
        while(n > 0){
            if(n%2)
                count++; //Increment count if the current bit is set.
            n /= 2; //Divide the number by 2 to move to the next bit.
        }

        return (count * N); //Return the product of count and N.
    }
};

class Solution
{
    public int bitMultiply(int N)
    {
        int count = 0, n = N;

        //Loop to count the number of set bits in the number N.
        while(n > 0){
            if(n%2 != 0)
                count++; //Increment count if the current bit is set.
            n /= 2; //Divide the number by 2 to move to the next bit.
        }

        return (count * N); //Return the product of count and N.
    }
}
