class Solution
{
    // Function to calculate the logarithm base 10 of a number
    public double Func(long x, long y)
    {
        double ans = 0.00;
        if (y == 0)
            ans = Math.Log10(1);
        else
            ans = y * Math.Log10(x);
        return ans;
    }

    // Function to compare the powers of two numbers
    public int greaterPower(long a, long b, long m, long n)
    {
        // Calculating the logarithmic values for the numbers
        double x = Func(a, b);
        double y = Func(m, n);
        if (x > y)
            return 1;
        else if (y > x)
            return 0;
        else
