//User function Template for Java
class Solution{
    static int countSCC(int a,int b){
        int ans=0;
        //if b can form at least half of a+2b, then take b as SCC units
		if((b*2)<=a){
			ans=b;
			a-=(b*2);
			ans+=a/4;
		}
        //otherwise, if a is greater than or equal to 2b, then take a/2 as SCC units
		else{
			ans=a/2;
			a-=(ans*2);
			ans+=a/4;
		}
		return ans;
    }
}
