// Back-end complete function Template for C++

class Solution {
  public:

    // Function to calculate the height or distance using given type, value, and angle.
    int findHeightOrDistance(char type, double value, double angle) {

        double result;

        // If type is 'h', calculate the height using the formula value/tan(angle).
        if (type == 'h')
            result = (value) / tan(angle * 3.14 / 180.0);
        // If type is 'd', calculate the distance using the formula value*tan(angle).
        else
            result = (value)*tan(angle * 3.14 / 180.0);

        // Return the floor value of the result.
        return floor(result);
    }
};

//User function Template for C#

class Solution
{
    //Complete this function
    //Function to find height or distance based on type, value and angle.
    public int findHeightOrDistance(char type, double value, double angle)
    {
        double result;

        // If type is 'h', calculate the height using the formula value/tan(angle).
        if (type == 'h')
            result = (value) / Math.Tan(angle * 3.14 / 180.0);
        // If type is 'd', calculate the distance using the formula value*tan(angle).
        else
            result = (value)*Math.Tan(angle * 3.14 / 180.0);

        // Return the floor value of the result.
        return (int)Math.Floor(result);
    }
}
