# Base class Employee
class Employee:

    def __init__(self, id, salary):
        self.id = id
        self.salary = salary


# Derived class SalesEmployee that extends Employee
class SalesEmployee(Employee):

    def __init__(self, id, salary, sales):
        super().__init__(
            id, salary)  # Initialize id and salary using the Employee class
        self.sales = sales
