// Back-end complete function Template for C++

class Solution {
  public:
    // Function to count the number of nodes in the i-th level of a binary tree
    int countNodes(int i) { return pow(2, (i - 1)); }
};

java.full_func:
class Solution {
    static int countNodes(int i) {
        // return the number of nodes in a binary tree of height i
        return (int)Math.pow(2, (i - 1));
    }
}

