#include <stdio.h>

int main() {
    int n;
    scanf("%d", &n);
    // code here
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n - i; j++) {
            printf("* ");
        }
        printf("\n");
    }
    return 0;
}

#include <iostream>
using namespace std;

int main() {
    int n;
    cin >> n;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n - i; j++) {
            cout << "* ";
        }
        cout << endl;
    }

    return 0;
}

