//Back-end complete function Template for Java

public static void invTriangleWall(int s){
    for(int i = 0; i < s; i++){
        for (int j = 0;j < s-i; j++){
            System.out.print("* ");
        }
        System.out.println();
    }

}

//User function Template for C#

class Solution{

    public void invTriangleWall(int s){

        for(int i = 0; i < s; i++){
            for (int j = 0;j < s-i; j++){
                Console.Write("* ");
            }
            Console.WriteLine();
        }

    }
}

