class Solution {
  public:
    // Function to check if a binary string is divisible by 3
    bool isDivisible(string& s) {
        int sum = 0, x = 1;

        // Iterating over the binary string from right to left
        for (int i = s.length() - 1; i >= 0; i--) {
            // Calculating the sum of the binary digits modulo 3
            if ((s[i] - '0') == 1) {
                sum = (sum + x) % 3;
            }

            // Updating x by multiplying it with 2 modulo 3
            x = (x * 2) % 3;
        }

        // Checking if the sum is equal to 0
        return (sum == 0);
    }
};

