class Solution{
    public:
    //Function to calculate the total amount of money.
    long long int total_Money(int n, int k){
        //Calculating the number of times k can be divided in n.
        long long int x = n/k;
        //Calculating the sum of numbers from 1 to x.
        long long int ans = x * (x + 1)/2;
        //Calculating the total amount of money.
        ans  = ans * k;
        //Returning the total amount of money.
        return ans;
    }    
};
