class Solution {

    // Method to read an integer from the user input using Scanner
    int javaIntType(Scanner sc) throws IOException{
        int A = sc.nextInt();
        return A;
    }

    // Method to read a string from the user input using Scanner
    String javaStringType(Scanner sc) throws IOException{
        String B = sc.next();
        return B;
    }

    // Method to read a float from the user input using Scanner
    float javaFloatType(Scanner sc) throws IOException{
        float C = sc.nextFloat();
        return C;
    }
}
