class GfG
{
	 // Inserts an entry with key x and value y in map
	void add_Value(HashMap<Integer,Integer> hm, int x, int y)
	{
		hm.put(x,y); // Adding key-value pair to the map
	}

	 // Returns the value with key x from the map if it has x, else return -1
	int find_value(HashMap<Integer, Integer> hm, int x)
	{
		int s=-1; // Initializing s as -1
		if(hm.containsKey(x)) // Checking if the map contains the key
			s = hm.get(x); // Getting the value associated with the key

		return s; // Returning the value
	}

	// Returns the size of the map
	int getSize(HashMap<Integer, Integer> hm)
	{
		return hm.size(); // Returning the size of the map
	}

	// Removes the entry with key x from the map	
	void removeKey(HashMap<Integer, Integer> hm, int x)
	{
		hm.remove(x); // Removing the key-value pair from the map
	}
}
