//Back-end complete function Template for Java

class Solution
{
    Queue<Integer>q;
    Solution(){
        q=new LinkedList<>();
    }
    public void addElement(int key)
    {
        q.add(key);
    }
    public void RemoveElement()
    {
        if(q.size()!=0)
        {int ele=q.remove();
        System.out.print(ele+" ");}
        else
        System.out.print("-1 ");
    }
    public void peekElement()
    {
        if(q.size()!=0)
        {int pE=q.peek();
        System.out.print(pE+" ");}
        else
        System.out.print("-1 ");
    }
    public void Size()
    {
        int queueSize=q.size();
        System.out.print(queueSize+" ");
    }
}
