//Back-end complete function Template for C++

class Solution {
  public:
    string toLower(string& s) {
        // size of the string is len
        int len = s.size();
        for (int i = 0; i < len; i++) {
            // checking for each charater it is upper or not if upper then converting it
            // to lower
            if (isupper(s[i]))
                s[i] = tolower(s[i]);
        }
        // returning converted string
        return s;
    }
};

// Back-end complete function for Java

class Solution {
    static String toLower(String s) {
        //converting uppercase to lowercase using inbuilt function toLowerCase() 
        String Converted_S = s.toLowerCase();
        //returning converted string
        return Converted_S;
    }
}

