//Back-end complete function Template for Java

class Solution {
    public int findMin(int a, int b) {
        int Min = Integer.MAX_VALUE;
        Min = Math.min(Min, a + b);
        Min = Math.min(Min, a - b);
        Min = Math.min(Min, a * b);
        try {
            Min = Math.min(Min, a / b);
        } catch (Exception e) {
        } finally {
            return Min;
        }
    }
}
