// Back-end complete function Template for Java

class Solution{
    // Function to map the keys and values into a HashMap
    static int map(int n, String keys[], int arr[], String s)
    {
        // Creating a HashMap to store the keys and values
        HashMap<String, Integer> mp = new HashMap<>();

        // Iterating through the keys and values array
        // and putting them into the HashMap
        for(int i = 0;i < n;i++)
            mp.put(keys[i], arr[i]);

        // Checking if the given key is present in the HashMap
        if(mp.containsKey(s))
            // Returning the corresponding value if key is present
            return mp.get(s);

        // Returning -1 if key is not present in the HashMap
        return -1;
    }
}
