class Solution{
    static ArrayList<Integer> getSum(int N){
        int sum_even=0,sum_odd = 0; //variables to store the sum of even and odd numbers
    	int counter = 1; //initialize a counter to 1
    	while(counter<=N) //iterate until the counter reaches N
    	{
    		if((counter%2) == 0) //check if the counter is even
    			sum_even+=counter; //add the counter to the sum of even numbers
    		else
    			sum_odd+=counter; //add the counter to the sum of odd numbers
    		counter++; //increment the counter by 1
    	}
    	ArrayList<Integer> ans = new ArrayList<>(); //create a new arraylist to store the sums
    	ans.add(sum_even); //add the sum of even numbers to the arraylist
    	ans.add(sum_odd); //add the sum of odd numbers to the arraylist
    	return ans; //return the arraylist with the sums
    }
}
